const handleEnterPress = useCallback(() => {
  if (!isRecording && stage === 0) {
    handleStartRecording();
  } else if (isRecording && stage === 0) {
    handleStopRecording();
  } else if (stage === 1) {
    setStage(2);
    setHighlightedText(highlightText(originalText, transformedText));
  } else if (stage === 2 && ttsUrl) {
    playAudio(ttsUrl);
    setStage(3);
  } else if (stage === 3 && !generatingImage && imageUrl) {
    setStage(4);
  } else if (stage === 4 && !generatingMusic && musicUrl) {
    playAudio(musicUrl);
    setStage(5);
  } else if ((stage === 4 && generatingImage) || (stage === 5 && generatingMusic)) {
    console.log('Still generating...');
  }
}, [isRecording, stage, ttsUrl, imageUrl, musicUrl, handleStartRecording, handleStopRecording, originalText, transformedText, generatingImage, generatingMusic]);
