import React, { useState, useRef, useEffect, useCallback } from 'react';

const App = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [stage, setStage] = useState(0);
  const [recognizedText, setRecognizedText] = useState('');
  const [originalText, setOriginalText] = useState('');
  const [transformedText, setTransformedText] = useState('');
  const [imageUrl, setImageUrl] = useState(null);
  const [ttsUrl, setTtsUrl] = useState(null);
  const [musicUrl, setMusicUrl] = useState(null);
  const [highlightedText, setHighlightedText] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const handleStartRecording = useCallback(() => {
    setIsRecording(true);
    audioChunksRef.current = [];

    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
      const mediaRecorder = new MediaRecorder(stream, { mimeType: 'audio/webm' });
      mediaRecorderRef.current = mediaRecorder;

      mediaRecorder.ondataavailable = event => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const formData = new FormData();
        formData.append('audio', audioBlob, 'audio.webm');

        fetch('http://127.0.0.1:5000/recognize', {
          method: 'POST',
          body: formData
        })
        .then(response => response.json())
        .then(data => {
          setRecognizedText(data.recognized_text);
          setOriginalText(data.original_text);
          setTransformedText(data.transformed_text);
          generateImage(data.transformed_text);
          generateMusic(data.transformed_text);
          textToSpeech(data.transformed_text);
          setStage(1);
        })
        .catch(error => console.error('Error:', error))
        .finally(() => setIsRecording(false));
      };

      mediaRecorder.start();
    }).catch(error => {
      console.error('Error accessing microphone:', error);
      setIsRecording(false);
    });
  }, []);

  const handleStopRecording = useCallback(() => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
    }
  }, [isRecording]);

  const handleEnterPress = useCallback(() => {
    if (!isRecording && stage === 0) {
      handleStartRecording();
    } else if (isRecording && stage === 0) {
      handleStopRecording();
    } else if (stage === 1) {
      setStage(2);
      setHighlightedText(highlightText(originalText, transformedText));
    } else if (stage === 2 && ttsUrl) {
      playAudio(ttsUrl);
      setStage(3);
    } else if (stage === 3 && imageUrl) {
      setStage(4);
    } else if (stage === 4 && musicUrl) {
      playAudio(musicUrl);
      setStage(5);
    }
  }, [isRecording, stage, ttsUrl, imageUrl, musicUrl, handleStartRecording, handleStopRecording, originalText, transformedText]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        handleEnterPress();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleEnterPress]);

  const playAudio = (url) => {
    const audio = new Audio(url);
    audio.play();
  };

  const generateImage = (transformedText) => {
    fetch('http://127.0.0.1:5000/generate-image', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ transformed_text: transformedText })
    })
    .then(response => response.json())
    .then(data => setImageUrl(data.imagePath))  // 変更点: data.imageUrl を data.imagePath に変更
    .catch(error => console.error('Error:', error));
  };

  const generateMusic = (transformedText) => {
    fetch('http://127.0.0.1:5000/generate-music', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ transformed_text: transformedText })
    })
    .then(response => response.json())
    .then(data => setMusicUrl(data.musicPath))
    .catch(error => console.error('Error:', error));
  };

  const textToSpeech = (transformedText) => {
    fetch('http://127.0.0.1:5000/text-to-speech', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ transformed_text: transformedText })
    })
    .then(response => response.json())
    .then(data => setTtsUrl(data.ttsPath))
    .catch(error => console.error('Error:', error));
  };

  const highlightText = (original, transformed) => {
    let highlighted = '';
    for (let i = 0; i < original.length; i++) {
      if (original[i] !== transformed[i]) {
        highlighted += `<span style="color: red;">${transformed[i] || ''}</span>`;
      } else {
        highlighted += `<span style="color: white;">${original[i]}</span>`;
      }
    }
    return highlighted;
  };

  return (
    <div style={{ backgroundColor: 'black', color: 'white', height: '100vh', padding: '20px' }}>
      <div
        tabIndex="0"
        style={{ whiteSpace: 'pre-wrap', outline: 'none' }}
      >
        {isRecording ? 'Recording...' : 'Press Enter to start recording'}
        {stage >= 1 && <div>Recognized Text: {recognizedText}</div>}
        {stage === 2 && <div dangerouslySetInnerHTML={{ __html: highlightedText }} />}
        {stage === 3 && ttsUrl && <audio src={`http://127.0.0.1:5000/generated/${ttsUrl}`} controls autoPlay />}
        {stage === 4 && imageUrl && <img src={`http://127.0.0.1:5000/generated/${imageUrl}`} alt="Generated" />}
        {stage === 5 && musicUrl && <audio src={`http://127.0.0.1:5000/generated/${musicUrl}`} controls autoPlay />}
      </div>
    </div>
  );
};

export default App;
